import { Component, OnInit } from '@angular/core';
import {Injectable} from '@angular/core';

@Component({
  selector: 'app-scripts',
  templateUrl: './scripts.component.html',
  styleUrls: ['./scripts.component.css']
})
@Injectable()
export class ScriptsComponent implements OnInit{

  constructor() {

  }
  public loadScript(id: string){
  	return new Promise((resolve, reject) => {
  		if(id && document.getElementById(id)){
  			resolve({id: id, loaded: true, status: 'Already Loaded'});
  		}
  		let body = document.body;
  		let script = document.createElement('script');
  		script.type = 'text/javascript';
  		script.innerHTML = '';
  		script.src = '';
  		script.id = id;
  		script.onload =() => {
  			resolve({id: id, loaded: true, status: 'Loaded'});
  		};
  		script.onerror = (error: any) => resolve({id: id, loaded: false, status: 'Loaded'});
  		script.async = true;
  		script.defer = true;
  		body.appendChild(script);
  	});
  }

  public removeScript(id: string){
  	let script = document.getElementById(id);
  	if(script){
  		script.remove();
  	}
  }

  ngOnInit(): void {
  	this.loadScript(id:'ejecutar_python');
  }

  noOnDestroy(): void{
  	this.removeScript('ejecutar_python');
  }

}
